const nameColorMap = {
  react: transparentize(0.95, "#61DAFB"),
  vue: transparentize(0.95, "#41B883"),
  graphql: transparentize(0.95, "#E10098"),
  gatsby: transparentize(0.95, "#744C9E"),
  next: transparentize(0.95, "#000"),
  git: transparentize(0.95, "#F05133"),
  node: transparentize(0.95, "#6ABF4B"),
  mongo: transparentize(0.95, "#599636"),
  javascript: transparentize(0.95, "#F7DF1E"),
  typescript: "#007ACC",
};

export const BoxStack = ({ name = "react" }: BoxStackProps) => {
  return (
    <Box background={nameColorMap[name]} padding={name !== "typescript" ? "10px" : 0}>
      <IconStack name={name} />
    </Box>
  );
};

export default BoxStack;
